import React, { Component } from "react";

class Contact extends Component{
    constructor() {
        super();

        this.state = {
            name: "",
            email: "",
            description: "",
            submitMessage: "",
            submitMessageTextColour: "",
        };
    }

    onChange = (event) => {
        // console.log(event.target.value);
        this.setState({
            [event.target.name]: event.target.value,
        });
    };

    OnSubmit = (event) => {
        event.preventDefault();

        let isSuccessful = true;
        const { name } = this.state;

        if(isSuccessful){
            this.state({
                submitMessage: 'Thank you &{ name }, I will contact you soon!',
                submitMessageTextColour: "text-success",
            })
        }
        else{
            this.state({
                submitMessage: 'Oops! something went wrong',
                submitMessageTextColour: "text-danger",
            })
        }
    };
    
    render() {
        const { submitMessageTextColour, submitMessage} = this.state;
        
        return (             
                <div className="container my-5">
                    <h1 className="font-weight-light text-center py-5">
                        <span className="text-info">Thank you! </span> for your interest
                    </h1>  
                    <div className="row justify-content-center">
                        <div className="col-11 col-lg-5">
                            <form onSubmit={this.Onsubmit}>
                                <div className="form-group">
                                    <label htmlFor="name">Name *</label> 
                                    <input type="text" name="name" className="form-control" onChange={this.onChange} />                               
                                </div>
                                <div className="form-group">
                                    <label htmlFor="email">Email *</label> 
                                    <input type="email" name="email" className="form-control" onChange={this.onChange} />                               
                                </div>
                                <div className="form-group">
                                    <label htmlFor="description">Tell me about your project *</label> 
                                    <textarea className="form-control text-muted" name="description"  rows="5" onChange={this.onChange}></textarea>                               
                                </div>
                                <br />
                                <br />
                                <button 
                                type="submit" className="btn btn-dark float-right"
                                style={{backgroundColor: "black"}}
                                >
                                "Let's talk bussiness"
                                </button>
                            </form>
                        </div>
                    </div>
                    <div className="py-5 mx-2 text-center">
                        {<h5 className= {submitMessageTextColour}>{submitMessage}</h5>}
                    </div>
                </div>
        );
    };
}

export default Contact;
